java -server -Xms4g -Xmx4g -XX:MaxDirectMemorySize=10g -XX:+AlwaysPreTouch -XX:+DisableExplicitGC -XX:+ScavengeBeforeFullGC -XX:GCHeapFreeLimit=5 -XX:+HeapDumpOnOutOfMemoryError -XX:HeapDumpPath=dump.hprof -XX:+UseG1GC -XX:MaxGCPauseMillis=200 -XX:GCPauseIntervalMillis=5000 -XX:+PrintGCDetails -XX:+PrintGCTimeStamps -XX:+PrintGCDateStamps -XX:+UseGCLogFileRotation -XX:NumberOfGCLogFiles=10 -XX:GCLogFileSize=100M -Xloggc:gc.log -jar messanger.jar